function greet(e){console.log("Hello, "+e+"!")}function welcomeMessage(e,o){o<18?console.log("Hey "+e+", you are still a minor!"):console.log("Welcome "+e+", you are an adult!")}function factorial(e){return 0===e?1:e*factorial(e-1)}let persons=[{name:"Alice",age:17},{name:"Bob",age:22},{name:"Charlie",age:16}];persons.forEach(e=>{greet(e.name),welcomeMessage(e.name,e.age),console.log(e.name+"'s factorial of age is: "+factorial(e.age))});